import React, { useRef, useMemo } from 'react';
import { Canvas, useFrame } from '@react-three/fiber';
import { OrbitControls, Sphere, Box, Torus, Cone } from '@react-three/drei';
import * as THREE from 'three';

// Floating museum artifact component
function MuseumArtifact({ position, geometry, color, rotationSpeed }) {
    const meshRef = useRef();

    useFrame((state) => {
        if (meshRef.current) {
            meshRef.current.rotation.x += rotationSpeed[0];
            meshRef.current.rotation.y += rotationSpeed[1];
            meshRef.current.position.y = position[1] + Math.sin(state.clock.elapsedTime * 0.5 + position[0]) * 0.3;
        }
    });

    return (
        <mesh ref={meshRef} position={position}>
            {geometry}
            <meshStandardMaterial
                color={color}
                transparent
                opacity={0.15}
                wireframe={false}
                metalness={0.8}
                roughness={0.2}
            />
        </mesh>
    );
}

// Particle system for depth
function Particles({ count = 100 }) {
    const points = useRef();

    const particlesPosition = useMemo(() => {
        const positions = new Float32Array(count * 3);
        for (let i = 0; i < count; i++) {
            positions[i * 3] = (Math.random() - 0.5) * 30;
            positions[i * 3 + 1] = (Math.random() - 0.5) * 30;
            positions[i * 3 + 2] = (Math.random() - 0.5) * 20;
        }
        return positions;
    }, [count]);

    useFrame((state) => {
        if (points.current) {
            points.current.rotation.y = state.clock.elapsedTime * 0.05;
        }
    });

    return (
        <points ref={points}>
            <bufferGeometry>
                <bufferAttribute
                    attach="attributes-position"
                    count={particlesPosition.length / 3}
                    array={particlesPosition}
                    itemSize={3}
                />
            </bufferGeometry>
            <pointsMaterial
                size={0.05}
                color="#6366f1"
                transparent
                opacity={0.3}
                sizeAttenuation
            />
        </points>
    );
}

// Interactive camera that follows mouse
function InteractiveCamera() {
    const cameraRef = useRef();

    useFrame((state) => {
        if (cameraRef.current) {
            const { mouse } = state;
            cameraRef.current.position.x = THREE.MathUtils.lerp(
                cameraRef.current.position.x,
                mouse.x * 2,
                0.05
            );
            cameraRef.current.position.y = THREE.MathUtils.lerp(
                cameraRef.current.position.y,
                mouse.y * 2,
                0.05
            );
            cameraRef.current.lookAt(0, 0, 0);
        }
    });

    return <perspectiveCamera ref={cameraRef} position={[0, 0, 10]} />;
}

// Main 3D Scene
function Scene() {
    return (
        <>
            {/* Lighting */}
            <ambientLight intensity={0.3} />
            <directionalLight position={[10, 10, 5]} intensity={0.5} />
            <pointLight position={[-10, -10, -5]} intensity={0.3} color="#3b82f6" />

            {/* Museum Artifacts */}
            <MuseumArtifact
                position={[-4, 2, -5]}
                geometry={<boxGeometry args={[1.5, 1.5, 1.5]} />}
                color="#3b82f6"
                rotationSpeed={[0.01, 0.01]}
            />
            <MuseumArtifact
                position={[4, -2, -8]}
                geometry={<torusGeometry args={[1, 0.4, 16, 100]} />}
                color="#6366f1"
                rotationSpeed={[0.005, 0.015]}
            />
            <MuseumArtifact
                position={[-3, -3, -6]}
                geometry={<sphereGeometry args={[1, 32, 32]} />}
                color="#8b5cf6"
                rotationSpeed={[0.008, 0.008]}
            />
            <MuseumArtifact
                position={[5, 3, -7]}
                geometry={<coneGeometry args={[1, 2, 32]} />}
                color="#4f46e5"
                rotationSpeed={[0.012, 0.006]}
            />
            <MuseumArtifact
                position={[0, 0, -10]}
                geometry={<torusGeometry args={[2, 0.3, 16, 100]} />}
                color="#2563eb"
                rotationSpeed={[0.003, 0.01]}
            />
            <MuseumArtifact
                position={[-5, 0, -9]}
                geometry={<boxGeometry args={[1, 2, 1]} />}
                color="#1d4ed8"
                rotationSpeed={[0.007, 0.009]}
            />

            {/* Particles for depth */}
            <Particles count={150} />
        </>
    );
}

// Main Background3D Component
export default function Background3D() {
    return (
        <div
            style={{
                position: 'fixed',
                top: 0,
                left: 0,
                width: '100vw',
                height: '100vh',
                zIndex: -1,
                pointerEvents: 'none',
            }}
        >
            <Canvas
                camera={{ position: [0, 0, 10], fov: 75 }}
                style={{ background: 'transparent' }}
            >
                <Scene />
            </Canvas>
        </div>
    );
}
