import React, { useEffect, useState } from "react";
import axios from "axios";

export default function TicketList() {
  const [tickets, setTickets] = useState([]);

  const fetchTickets = async () => {
    try {
      const res = await axios.get("http://localhost:5000/api/tickets");
      setTickets(res.data);
    } catch (err) {
      console.error("Failed to fetch tickets:", err);
    }
  };

  useEffect(() => {
    fetchTickets();
    const intervalId = setInterval(fetchTickets, 5000); // Poll for new tickets
    return () => clearInterval(intervalId);
  }, []);

  return (
    <div className="bg-white p-4 rounded shadow-lg border border-gray-200 h-96 overflow-y-auto">
      <h4 className="font-semibold mb-2">Recent Bookings</h4>
      {tickets.length === 0 ? (
        <div className="text-sm text-gray-500">No tickets booked yet.</div>
      ) : (
        <ul className="space-y-2">
          {tickets.map(t => (
            <li key={t._id} className="p-2 border rounded bg-gray-50">
              <div className="text-sm font-bold">{t.ticketType}</div>
              <div className="text-xs text-gray-700">Exhibition: <strong>{t.exhibition}</strong></div>
              <div className="text-xs text-gray-600">Date: {t.date} • Visitors: {t.visitors} • Category: {t.category}</div>
              <div className="text-xs text-green-700 font-semibold">Status: {t.status}</div>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
}
