import React, { useState, useEffect, useRef } from "react";
import axios from "axios";

const BOT = "Bot";
const USER = "You";

export default function Chatbot() {
  const [messages, setMessages] = useState([
    { sender: BOT, text: "Welcome to the Museum Booking Bot! Type 'book' to start." }
  ]);
  const [input, setInput] = useState("");
  const [context, setContext] = useState({ step: 0, data: {} });
  const endRef = useRef(null);

  useEffect(() => { endRef.current?.scrollIntoView({ behavior: "smooth" }); }, [messages]);

  const pushBot = (text) => setMessages(prev => [...prev, { sender: BOT, text }]);
  const pushUser = (text) => setMessages(prev => [...prev, { sender: USER, text }]);

  const reset = () => setContext({ step: 0, data: {} });

  const handleSend = async () => {
    if (!input.trim()) return;
    const text = input.trim();
    pushUser(text);
    setInput("");

    // State machine for museum booking flow
    if (context.step === 0) {
      if (text.toLowerCase().includes("book")) {
        pushBot("Great! What type of ticket? (e.g., General Admission, Special Exhibition)");
        setContext({ step: 1, data: {} });
      } else {
        pushBot("Type 'book' to start a new ticket booking or ask for help.");
      }
      return;
    }

    const newData = { ...context.data };

    if (context.step === 1) {
      newData.ticketType = text;
      pushBot("Which exhibition would you like to see? (e.g., Main Collection, Modern Art)");
      setContext({ step: 2, data: newData });
      return;
    }

    if (context.step === 2) {
      newData.exhibition = text;
      pushBot("Enter the date of your visit (YYYY-MM-DD)");
      setContext({ step: 3, data: newData });
      return;
    }

    if (context.step === 3) {
      newData.date = text;
      pushBot("How many visitors?");
      setContext({ step: 4, data: newData });
      return;
    }

    if (context.step === 4) {
      const v = parseInt(text) || 1;
      newData.visitors = v;
      pushBot("Which category? (e.g., Adult, Child, Student)");
      setContext({ step: 5, data: newData });
      return;
    }

    if (context.step === 5) {
      newData.category = text;
      // Simple price calculation
      const basePrice = newData.ticketType.toLowerCase().includes("special") ? 25 : 15;
      newData.price = basePrice * (newData.visitors || 1);

      pushBot(`Booking summary:\nTicket: ${newData.ticketType}\nExhibition: ${newData.exhibition}\nDate: ${newData.date}\nVisitors: ${newData.visitors}\nCategory: ${newData.category}\nPrice: $${newData.price}\n\nType 'confirm' to pay & book.`);
      setContext({ step: 6, data: newData });
      return;
    }

    if (context.step === 6) {
      if (text.toLowerCase() === "confirm") {
        try {
          const payload = { name: "Guest", ...context.data };
          await axios.post("http://localhost:5000/api/tickets", payload);
          pushBot("Ticket booked successfully! Your ticket appears on the right.");
          reset();
        } catch (err) {
          console.error(err);
          pushBot("Booking failed. Please try again later.");
        }
      } else {
        pushBot("Type 'confirm' to finalize booking or 'cancel' to abort.");
      }
      return;
    }
  };

  return (
    <div className="bg-white p-6 rounded-xl shadow-lg border border-gray-200">
      <h3 className="text-lg font-bold mb-3">üèõÔ∏è Museum Chatbot</h3>
      <div className="h-96 overflow-y-auto p-3 mb-3 border rounded" style={{ background: "#f9fafb" }}>
        {messages.map((m, i) => (
          <div key={i} className={`mb-2 ${m.sender === USER ? 'text-right' : ''}`}>
            <div className={`inline-block p-2 rounded ${m.sender === USER ? 'bg-green-100' : 'bg-gray-100'}`}>
              <strong className="block text-xs">{m.sender}</strong>
              <div style={{ whiteSpace: 'pre-wrap' }}>{m.text}</div>
            </div>
          </div>
        ))}
        <div ref={endRef} />
      </div>

      <div className="flex gap-2">
        <input
          className="flex-1 p-2 border rounded"
          value={input}
          onChange={(e) => setInput(e.target.value)}
          onKeyDown={(e) => e.key === 'Enter' && handleSend()}
        />
        <button className="px-4 py-2 bg-blue-600 text-white rounded" onClick={handleSend}>Send</button>
      </div>
    </div>
  );
}
