import React, { useState } from "react";
import API from "../api/axios";

export default function TicketCard({ ticket, onUpdate }) {
    const [showDetails, setShowDetails] = useState(false);
    const [canceling, setCanceling] = useState(false);
    const [showCancelModal, setShowCancelModal] = useState(false);
    const [cancelReason, setCancelReason] = useState("");

    const handleCancelTicket = async () => {
        try {
            setCanceling(true);
            const response = await axios.post(
                `http://localhost:5000/api/tickets/${ticket._id}/cancel`,
                { reason: cancelReason }
            );

            if (response.data.success) {
                alert(response.data.message);
                setShowCancelModal(false);
                if (onUpdate) onUpdate();
            }
        } catch (err) {
            console.error("Error cancelling ticket:", err);
            alert(err.response?.data?.message || "Failed to cancel ticket");
        } finally {
            setCanceling(false);
        }
    };

    const getStatusColor = (status) => {
        switch (status) {
            case "Booked":
            case "Confirmed":
                return "bg-green-100 text-green-800 border-green-300";
            case "Cancelled":
                return "bg-red-100 text-red-800 border-red-300";
            case "Refunded":
                return "bg-blue-100 text-blue-800 border-blue-300";
            default:
                return "bg-gray-100 text-gray-800 border-gray-300";
        }
    };

    const canCancel = () => {
        if (ticket.status === "Cancelled" || ticket.status === "Refunded") return false;

        const visitDate = new Date(ticket.date);
        const today = new Date();
        const daysDifference = Math.ceil((visitDate - today) / (1000 * 60 * 60 * 24));

        return daysDifference >= 2;
    };

    return (
        <>
            <div className="bg-white rounded-xl shadow-md overflow-hidden hover:shadow-xl transition-all duration-300 border border-gray-100">
                <div className="bg-gradient-to-r from-blue-600 to-indigo-600 p-4 text-white">
                    <div className="flex justify-between items-start">
                        <div className="flex-1">
                            <h3 className="text-xl font-bold mb-1">{ticket.ticketType}</h3>
                            <p className="text-blue-100 text-sm">{ticket.exhibition}</p>
                        </div>
                        <span className={`px-3 py-1 rounded-full text-xs font-semibold border ${getStatusColor(ticket.status)}`}>
                            {ticket.status}
                        </span>
                    </div>
                </div>

                <div className="p-5">
                    <div className="grid grid-cols-2 gap-4 mb-4">
                        <div>
                            <p className="text-gray-500 text-xs uppercase mb-1">Visit Date</p>
                            <p className="text-gray-900 font-semibold">ðŸ“… {ticket.date}</p>
                        </div>
                        <div>
                            <p className="text-gray-500 text-xs uppercase mb-1">Visitors</p>
                            <p className="text-gray-900 font-semibold">ðŸ‘¥ {ticket.visitors} {ticket.category}</p>
                        </div>
                        <div>
                            <p className="text-gray-500 text-xs uppercase mb-1">Total Price</p>
                            <p className="text-blue-600 font-bold text-lg">ðŸ’µ ${ticket.price}</p>
                        </div>
                        <div>
                            <p className="text-gray-500 text-xs uppercase mb-1">Payment</p>
                            <p className="text-gray-900 font-semibold">{ticket.paymentStatus || "Paid"}</p>
                        </div>
                    </div>

                    <div className="flex gap-2">
                        <button
                            onClick={() => setShowDetails(!showDetails)}
                            className="flex-1 bg-gray-100 hover:bg-gray-200 text-gray-700 font-semibold py-2 px-4 rounded-lg transition-colors"
                        >
                            {showDetails ? "Hide Details" : "View Details"}
                        </button>

                        {canCancel() && (
                            <button
                                onClick={() => setShowCancelModal(true)}
                                className="flex-1 bg-red-50 hover:bg-red-100 text-red-600 font-semibold py-2 px-4 rounded-lg transition-colors border border-red-200"
                            >
                                Cancel Ticket
                            </button>
                        )}
                    </div>

                    {showDetails && (
                        <div className="mt-4 pt-4 border-t border-gray-200 space-y-2 text-sm">
                            <div className="flex justify-between">
                                <span className="text-gray-600">Ticket ID:</span>
                                <span className="text-gray-900 font-mono text-xs">{ticket._id}</span>
                            </div>
                            <div className="flex justify-between">
                                <span className="text-gray-600">Name:</span>
                                <span className="text-gray-900">{ticket.name || "Guest"}</span>
                            </div>
                            <div className="flex justify-between">
                                <span className="text-gray-600">Email:</span>
                                <span className="text-gray-900">{ticket.email}</span>
                            </div>
                            <div className="flex justify-between">
                                <span className="text-gray-600">Booked on:</span>
                                <span className="text-gray-900">{new Date(ticket.createdAt).toLocaleDateString()}</span>
                            </div>
                            {ticket.cancelledAt && (
                                <div className="flex justify-between">
                                    <span className="text-gray-600">Cancelled on:</span>
                                    <span className="text-red-600">{new Date(ticket.cancelledAt).toLocaleDateString()}</span>
                                </div>
                            )}
                            {ticket.refundAmount > 0 && (
                                <div className="flex justify-between">
                                    <span className="text-gray-600">Refund Amount:</span>
                                    <span className="text-green-600 font-bold">${ticket.refundAmount}</span>
                                </div>
                            )}
                        </div>
                    )}
                </div>
            </div>

            {/* Cancel Modal */}
            {showCancelModal && (
                <div className="fixed inset-0 bg-black/50 backdrop-blur-sm z-50 flex items-center justify-center p-4">
                    <div className="bg-white rounded-2xl shadow-2xl w-full max-w-md overflow-hidden">
                        <div className="bg-red-600 p-6 text-white">
                            <h3 className="text-xl font-bold">Cancel Ticket</h3>
                            <p className="text-red-100 text-sm mt-1">Are you sure you want to cancel this booking?</p>
                        </div>

                        <div className="p-6">
                            <div className="bg-yellow-50 border-l-4 border-yellow-400 p-4 mb-4">
                                <p className="text-sm text-yellow-800">
                                    <strong>Refund Policy:</strong><br />
                                    â€¢ Full refund (100%) if cancelled 7+ days before visit<br />
                                    â€¢ 50% refund if cancelled 2-6 days before visit<br />
                                    â€¢ No refund if cancelled less than 2 days before
                                </p>
                            </div>

                            <label className="block text-sm font-semibold text-gray-700 mb-2">
                                Reason for Cancellation (Optional)
                            </label>
                            <textarea
                                value={cancelReason}
                                onChange={(e) => setCancelReason(e.target.value)}
                                className="w-full border border-gray-300 rounded-lg p-3 focus:ring-2 focus:ring-red-500 focus:border-red-500 outline-none mb-4"
                                rows="3"
                                placeholder="Please tell us why you're cancelling..."
                            />

                            <div className="flex gap-3">
                                <button
                                    onClick={() => setShowCancelModal(false)}
                                    disabled={canceling}
                                    className="flex-1 bg-gray-200 hover:bg-gray-300 text-gray-700 font-semibold py-3 rounded-lg transition-colors"
                                >
                                    Keep Ticket
                                </button>
                                <button
                                    onClick={handleCancelTicket}
                                    disabled={canceling}
                                    className="flex-1 bg-red-600 hover:bg-red-700 text-white font-semibold py-3 rounded-lg transition-colors disabled:opacity-50 disabled:cursor-not-allowed"
                                >
                                    {canceling ? "Cancelling..." : "Confirm Cancellation"}
                                </button>
                            </div>
                        </div>
                    </div>
                </div>
            )}
        </>
    );
}
